exec("import random \nprint('\\n WELCOME TO ROCK, PAPER, SCISSORS \\nThis match is against a computer and consists of 3 rounds \\nMay the odds be ever in your favor') \nround_track, player, computer = (1, 0, 0) \ndef game():\n\tglobal round_track, player, computer \n\tchosen = {'1' : 'Rock','2' : 'Paper','3' : 'Scissors'} \n\tp1 = input('\\nPlease press 1 for Rock, 2 for Paper, or 3 for Scissors - ') \n\tcomp = str(random.randint(1, 3)) \n\tprint(f'\\n Player chose {chosen[p1]} \\n Computer chose {chosen[comp]}') \n\tround_track += 1 \n\tif p1 == comp :\n\t\tprint('\\n_TIE_')\n\telif p1 == '1' and comp == '3' or p1 == '2' and comp == '1' or p1 == '3' and comp == '2':\n\t\tprint('\\n_PLAYER_WINS_')\n\t\tplayer += 1\n\telif comp == '1' and p1 == '3' or comp == '2' and p1 == '1' or comp == '3' and p1 == '2':\n\t\tprint('\\n_COMPUTER_WINS_')\n\t\tcomputer += 1 \nwhile True : \n\ttry: \n\t\tprint(f'\\n_ _ _ _ROUND {str(round_track)}_ _ _ _ \\n Player Score - - - {str(player)} \\n Computer Score - - - {str(computer)}') \n\t\tgame() \n\t\tif player ==2: \n\t\t\tprint('\\n\\n______ GAME OVER ______\\n___ VICTOR : PLAYER ___')\n\t\t\tbreak \n\t\telif computer ==2: \n\t\t\tprint('\\n\\n______ GAME OVER ______\\n__ VICTOR : COMPUTER __')\n\t\t\tbreak \n\texcept KeyError: \n\t\tprint('PLEASE CHOOSE A VALID OPTION')")